import { nanoid } from 'nanoid';

const Finger = ({ step }) => {
  const finger = {};
  if (step.fret === -1) {
    finger.vertical = '0';
  } else if (step.fret === 0) {
    finger.vertical = '0';
  } else if (step.fret === 1) {
    finger.vertical = '25%';
  } else if (step.fret === 2) {
    finger.vertical = '50%';
  } else if (step.fret === 3) {
    finger.vertical = '75%';
  } else if (step.fret === 4) {
    finger.vertical = '100%';
  }

  if (step.finger === 0) {
    finger.isFinger = false;
  } else if (step.finger > 0) {
    finger.isFinger = true;
  }

  return finger.isFinger && <div style={{ width: '16.6px', height: '20px', background: 'red' }}></div>;
};

export const ChordGuitar = ({ steps }) => {
  return (
    <div
      style={{
        width: ' 100px',
        height: '100px',
        background: '#D3F6EE',
        display: 'flex',
        justifyContent: 'space-between',
      }}
    >
      {steps.map((step) => {
        return <Finger key={nanoid()} step={step} />;
      })}
    </div>
  );
};
