// import { GuitarTab } from '../GuitarTab/GuitarTab';
import { useState } from 'react';
import tabs from '../../db.json';
// console.log(tabs.chords.A);

const findChord = (key, suffix) => {
  return tabs.chords[key].find((chord) => chord.suffix === suffix);
};
// console.log(findChord('D', 'minor'));

export const GuitarTabsList = () => {
  const [findedChords, setFindedChords] = useState([]);
  const onSubmit = (e) => {
    e.preventDefault();
    const key = e.target.key.value;
    const suffix = e.target.suffix.value;
    setFindedChords(findChord(key, suffix));
    findedChords.map((e) => console.log(e));
  };
  return (
    <>
      <form onSubmit={onSubmit} action="">
        <label htmlFor="">
          KEY
          <input type="text" name="key" />
        </label>

        <label htmlFor="">
          SUFFIX
          <input type="text" name="suffix" />
        </label>
        <button type="submit">O</button>
      </form>
      <div>
        <ul>
          {findedChords > 0 &&
            findedChords.positions.map((c) => {
              return <li>aaaaaa</li>;
            })}
        </ul>
      </div>
    </>
  );
};

