  //     resultSuffixes.push({
  //       suffix: allSuffix[i],
  //       isActive: false,
  //     });
  //   } else {
  //     resultSuffixes.push({
  //       suffix: allSuffix[i],
  //       isActive: true,
  //     });
  //   }
  // }

  return (
    <div>
      <ListSuffixes>
        {resultSuffixes.map((ch) => {
          // console.log(suffix);
          return (
            <ListItem key={ch.suffix} onClick={onClick} active={ch.isActive}>
              {ch.suffix}
            </ListItem>
          );
        })}
      </ListSuffixes>
    </div>
  );
};
