import MIDISounds from 'midi-sounds-react';
import React, { Component } from 'react';

export const WebMidi = () => {
  const playTestInstrument = (chord) => {
    MIDISounds.playChordNow(3, chord, 2.5);
  };
  return (
    <div className="App">
      <button onClick={playTestInstrument([55])}>Play</button>

      <MIDISounds ref={(ref) => (this.midiSounds = ref)} appElementName="root" instruments={[3]} />
    </div>
  );
};

export class WebMidio extends Component {
  playTestInstrument(chord) {
    this.midiSounds.playChordNow(3, chord, 2.5);
  }
  render() {
    return (
      <div className="App">
        <button onClick={this.playTestInstrument.bind(this)}>Play</button>

        <MIDISounds ref={(ref) => (this.midiSounds = ref)} appElementName="root" instruments={[3]} />
      </div>
    );
  }
}
