import db from './db.json';
import Block from './infoPlaste';
// console.log(Block());
// console.log(Block);

function App() {
  console.log(db.results);
  // CreateFilm(db.results);
  return (
    <>
      {db.results.map((item) => (
        <>
          <CreateCard key={item.id} id={item.id} name={item.title} />
          <GetPicture key={item.original_title} alt={item.title} imgSrc={item.poster_path} />
        </>
      ))}
    </>
  );
}

function CreateCard(props) {
  return (
    <li className="item">
      {props.id}
      {props.name}
    </li>
  );
}

function GetPicture(props) {
  return <img alt={props.alt} width="150" src={`http://image.tmdb.org/t/p/w780${props.imgSrc}`} />;
}

function CreateFilm(arr) {
  return (
    <>
      {db.results.map((item) => (
        <>
          {/* {console.log(item.id)} */}
          <CreateCard key={item.id} id={item.id} name={item.title} />
          <GetPicture key={item.title} alt={item.title} imgSrc={item.poster_path} />
        </>
      ))}
    </>
  );
}

export default App;
