import { nanoid } from 'nanoid';

const Position = ({ pos }) => {
  const steps = [];
  for (let index = 0; index < pos.fingers.length; index++) {
    steps.push({ fret: pos.frets[index], finger: pos.fingers[index] });
    steps[`step${index + 1}`].fret = pos.frets[index];
    steps[`step${index + 1}`].finger = pos.fingers[index];
  }
  const keys = Object.keys(steps);
  for (const key of keys) {
    console.log(steps[key].fret);
  }
  return (
    <>
      <h3>Position</h3>
      <ul>
        <li></li>
      </ul>
    </>
  );
};

export const GuitarTabsList = ({ chord }) => {
  return (
    <>
      <div>
        <h1>Guitar</h1>
        <h2>
          {chord.key}_{chord.suffix}
        </h2>
        <ul>
          {Object.keys(chord).length > 0 &&
            chord.positions.map((position) => {
              return (
                <li key={nanoid()}>
                  <Position pos={position} />
                </li>
              );
            })}
        </ul>
      </div>
    </>
  );
};
