import { nanoid } from 'nanoid';

const Position = ({ pos }) => {
  const steps = {
    step1: {
      fret: null,
      finger: null,
    },
    step2: {
      fret: null,
      finger: null,
    },
    step3: {
      fret: null,
      finger: null,
    },
    step4: {
      fret: null,
      finger: null,
    },
    step5: {
      fret: null,
      finger: null,
    },
    step6: {
      fret: null,
      finger: null,
    },
  };
  for (let index = 0; index < pos.fingers.length; index++) {
    // console.log(pos.fingers[index]);
    steps[`step${index + 1}`].fret = pos.frets[index];
    steps[`step${index + 1}`].finger = pos.fingers[index];
  }
};

export const GuitarTabsList = ({ chord }) => {
  return (
    <>
      <div>
        <h1>Guitar</h1>
        <h2>
          {chord.key}_{chord.suffix}
        </h2>
        <ul>
          {Object.keys(chord).length > 0 &&
            chord.positions.map((position) => {
              return (
                <li key={nanoid()}>
                  <Position pos={position} />
                </li>
              );
            })}
        </ul>
      </div>
    </>
  );
};
