import css from '../filmCard/PositionsList.module.css';

export function PositionsList({ chord }) {
  const str = chord;
  const fret = chord;

  doNote(chord);
  return dotableTab(5, 3);
}

function doNote() {}

function dotableTab(string, fret) {
  console.log(string);
  let markup = ``;
  for (let i = 0; i < fret; i++) {
    markup += `<tr className={css.fret}>
          <td>{string === 1 ? 'X' : '0'}</td>
          <td>{string === 2 ? 'X' : '0'}</td>
          <td>{string === 3 ? 'X' : '0'}</td>
          <td>{string === 4 ? 'X' : '0'}</td>
          <td>{string === 5 ? 'X' : '0'}</td>
          <td>{string === 6 ? 'X' : '0'}</td>
        </tr>`;
  }
  return (
    <>
      <table>{markup}</table>
    </>
  );
}
