import tabs from '../../db.json';
import { ListSuffixes, ListItem } from './NavigationSuffix.styled';

export const NavigationSuffix = ({ ss, k }) => {
  const onClick = (event) => {
    ss(event.target.outerText);
  };

  // console.log(tabs.chords.A);
  let allSuffix = tabs.suffixes;
  let activeSuffix = tabs.chords[k].map((chord) => {
    return chord.suffix;
  });
  // console.log(activeSuffix);

  const resultSuffixes = [];

  for (let i = 0; i < allSuffix.length; i++) {
    if (allSuffix.find((option) => option[i] === activeSuffix[i])) {
      resultSuffixes.push({
        suffix: allSuffix[i],
        isActive: false,
      });
    } else {
      resultSuffixes.push({
        suffix: allSuffix[i],
        isActive: true,
      });
    }
  }

  function test(arr) {
    for (let key in allSuffix) {
      if (allSuffix[key].every((letter) => arr.includes(letter))) {
        console.log('yes');
        return true;
      }
    }
    console.log('no');
    return false;
  }

  test();

  return (
    <div>
      <ListSuffixes>
        {resultSuffixes.map((ch) => {
          // console.log(suffix);
          return (
            <ListItem key={ch.suffix} onClick={onClick} active={ch.isActive}>
              {ch.suffix}
            </ListItem>
          );
        })}
      </ListSuffixes>
    </div>
  );
};
